INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('7','6','WAGO','wago','Weekly Asynchrony Golf Outing, or WAGO, is a group which meets during the Spring, Summer and Fall months to golf at Forest Park\\\'s Probstein golf course.','public','0','2013-06-11 16:57:22');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('2','169','FFASHH','ffashh','First Friday Asynchrony Happy Hour, or FFASHH, is a social club which meets every First Friday of the month to create new social connections and strengthen existing ones. Craft breweries and small pubs are generally selected as the meeting grounds.','public','0','2013-06-04 18:46:35');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('3','156','Halo','halo-players','Debatably the longest-standing club at Asynchrony, the Halo crew meets during lunch and after work to rock their favorite FPS.','public','0','2013-06-04 21:56:04');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('8','6','Agile Retro Facilitators','agile-retro-facilitators','Asynchrony Retro Facilitators meet up to share best practices on how to properly facilitate retro meetings in order to gain the most value for our teams.','public','0','2013-06-11 16:59:26');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('9','6','Game Night','game-night','Game Night is devoted to creating a social event for Asynchronites to come together and test their game wits against their peers.','public','0','2013-06-11 17:00:07');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('10','6','Mentors','mentors','Asynchrony Mentors are dedicated to creating a great first experience for all new Asynchrony employees. This can include mentor lunches, pairing or even extra-curricular actives such as kickball, biking or frisbee.','public','0','2013-06-11 17:00:51');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('11','6','Mobile Dev','mobile-dev','The Mobile Development group is interested in sharing knowledge related to app development on mobile platforms. All popular platforms have been covered within the past.','public','0','2013-06-11 17:01:42');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('12','6','Web Guild','web-guild','The Web Guild is a collection of Asynchronites who are interested in improving their knowledge of web development and best practices. All current technologies and methods have been covered throughout their meetings.','public','0','2013-06-11 17:02:24');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('13','6','Wellness','wellness','The Wellness club is focused on maintaining or improving the physical health and wellness of our organization. Topics covered may include running, biking, swimming or sharing of FitBit information.','public','0','2013-06-11 17:02:58');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('14','6','Agile Boot Camp','agile-boot-camp','.Asynchrony Bootcamp include Asynchronites whose mission is to create an cohesive and meaningful orientation and on-boarding experience for new hires.','public','0','2013-06-11 17:03:43');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('15','6','AC2','ac%c2%b2','The Asynchrony Culture Committee, or AC2, is an internal group dedicated to identifying opportunities for organizational growth and improvement.','public','0','2013-06-11 17:09:03');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('16','6','WOA','woa','The Women of Asynchrony, or WOA, is a group dedicated to creating stronger bonds between those Asynchronites rocking two x-chromosomes.','public','0','2013-06-11 17:23:01');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('17','6','Intranet Feedback','intranet-feedback','Intranet Feedback is dedicated to hearing your voice and suggestions for how to improve our intranet experience.','public','0','2013-06-11 17:23:50');
INSERT INTO `wp_bp_groups` (`id`,`creator_id`,`name`,`slug`,`description`,`status`,`enable_forum`,`date_created`) VALUES ('18','6','Lunch and Learn','lunch-and-learn','Lunch and Learn is a group which brings together the backgrounds and experiences of our peers to help provide knowledge and perspective to other Asynchronites. Topics covered have ranged from building Artificial Intelligence systems to music appreciation sessions.','public','0','2013-06-11 18:21:32');
